import { useState } from 'react'

const App = () => {
// const [left, setLeft] = useState(0)
// const [right, setRight] = useState(0)
const [click, setClick] = useState(
  {left: 0,
  right: 0
  }
)
const [list, setList] = useState([])

const leftClick = () => {
  newClick = {...click, left: click.left + 1}
  setClick(newClick)
} 
  // setLeft(left + 1)
  // setList(list.concat('L'))


const rightClick = () => {
  newClick = {...click, righr: click.right + 1} 
  setClick(newClick)
}
   // setRight(right + 1)
  // setList(list.concat('R'))

const reset = () => {
  setClick({left: 0, right: 0})
  setList([])
}

return(
  <div>
    <button onClick={leftClick}>left</button><p>Left: {left}</p>

    <button onClick={rightClick}>right</button><p>Right: {right}</p>
    <p>List: {list.join('   ')}</p>

    <button onClick={reset}>reset</button>
  </div>
)

}

export default App;
