import { useState } from 'react'

const Header = ({text}) => <h1>{text}</h1>
const Display = ({value, label}) => <div>{label} {value}</div>
const Button = ({handleClick, text}) => <button onClick={handleClick}>{text}</button>

const Statistics = ({good, neutral, bad}) => {

  const total = good + neutral + bad
  const average = () => (good - bad) / total
  const positiveFb = () => (good / total) * 100
  console.log(good, bad, neutral)
  return(
    <div>
      <Header text={'Statistics'} />
      <Display value={good} label={'Good: '} />
      <Display value={neutral} label={'Neutral: '} />
      <Display value={bad} label={'Bad: '} />
    </div>
  )
}

const App = (evaluations) => {
  const[good, setGood] = useState(0)
  const[neutral, setNeutral] = useState(0)
  const[bad, setBad] = useState(0)

  const addRatingGood = () => {
    setGood(good + 1)
  }
  const addRatingNeutral = () => {
    setNeutral(neutral + 1)
  }
  const addRatingBad = () => {
    setBad(bad + 1)
  } 

  return(
    <div>
      <Header text={'Give Feedback'} />
      <Button handleClick={addRatingGood} text={'Good'} />
      <Button handleClick={addRatingNeutral} text={'Neutral'} />
      <Button handleClick={addRatingBad} text={'Bad'} />
      <Statistics />
    </div>
  )
}

export default App;
