import { useState } from 'react'

const History = (props) => {
  if (props.allClicks.length === 0) {
    return (
      <div>
        the app is used by pressing the buttons
      </div>
    )
  }
  return (
    <div>
      button press history: {props.allClicks.join(' ')}
    </div>
  )
}

const button = ({handleClick, text}) => {
  <button onClick={handleClick}>{text}</button>
}

const App = () => {
const [click, setClick] = useState(
  {left: 0,
  right: 0
  }
)
const [list, setList] = useState([])
const [total, setTotal] = useState(0)

const leftClick = () => {
  setClick({...click, left: click.left + 1})
  setList(list.concat('L'))
  setTotal(click.left + click.right)
} 

const rightClick = () => {
  setClick({...click, right: click.right + 1})
  setList(list.concat('R'))
  setTotal(click.left + click.right)
}

const reset = () => {
  setClick({left: 0, right: 0})
  setList([])
  setTotal(0)
}

return(
  <div>
    <button onClick={leftClick}>left</button><p>Left: {click.left}</p>

    <button onClick={rightClick}>right</button><p>Right: {click.right}</p>
    <p>Total clicks: {total}</p>
    <History allClicks={list} />

    <button onClick={reset}>reset</button>
  </div>
)

}

export default App;
