import { useState } from 'react'

const App = () => {
const [left, setLeft] = useState(0)
const [right, setRight] = useState(0)
const [list, setList] = useState([])

const leftClick = () => {
  setLeft(left + 1)
  setList(list.concat('L'))
}

const rightClick = () => {
  setRight(right + 1)
  setList(list.concat('R'))
}

const reset = () => {
  setLeft(0)
  setRight(0)
  setList([])
}

return(
  <div>
    <button onClick={leftClick}>left</button><p>{left}</p>

    <button onClick={rightClick}>right</button><p>{right}</p>

    <button onClick={reset}>reset</button>
  </div>
)

}

export default App;
