import { useState } from 'react'

const Button = ({handleclick, text}) => <button onClick={handleclick}>{text}</button>     // button object
const Header = ({text}) => <h1>{text}</h1>
function getRandomIntInclusive(min, max) {                                                //random number between range
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min + 1) + min);
}

const votesArray = [0, 0, 0, 0, 0, 0, 0, 0]
const updatedArray = [...votesArray]

const App = () => {
  const anecdotes = [
    'If it hurts, do it more often.',
    'Adding manpower to a late software project makes it later!',
    'The first 90 percent of the code accounts for the first 10 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.',
    'Any fool can write code that a computer can understand. Good programmers write code that humans can understand.',
    'Premature optimization is the root of all evil.',
    'Debugging is twice as hard as writing the code in the first place. Therefore, if you write the code as cleverly as possible, you are, by definition, not smart enough to debug it.',
    'Programming without an extremely heavy use of console.log is same as if a doctor would refuse to use x-rays or blood tests when diagnosing patients.',
    'The only way to go fast, is to go well.'
  ]
  const [selected, setSelected] = useState(0)
  const [vote, setVote] = useState(votesArray)
  const [mostVoted, setMostVoted] = useState(0)

  const mostVotedUpdated = () => {
    console.log('index of', vote.indexOf(Math.max(vote)))
    setMostVoted(vote.indexOf(Math.max(vote)))
    return(
      mostVoted
    )
    }

  const anedocteSelector = () => setSelected(getRandomIntInclusive(0 , 7))

  const voteAnedocte = () => {
    updatedArray[selected] += 1
    setVote(updatedArray)
    console.log('vote array: ', vote)
    mostVotedUpdated();
  }

  // console.log('selected anecdote', selected)
  console.log('Max index:', mostVoted)

  return (
    <div>
      <Header text={'Anecdote of the day'} />
      <Button handleclick={anedocteSelector} text={'Click for an anedocte'} />
      <Button handleclick={voteAnedocte} text={"Vote"} />
      <div>
        {anecdotes[selected]}
      </div>
      <Header text={'Most voted anecdote'} />
        {anecdotes[mostVoted]}
      </div>
  )
}

export default App
