import { useState } from 'react'

const Display = ({value, label}) => <div>{label} {value}</div>
const Button = ({handleClick, text}) => <button onClick={handleClick}>{text}</button>

const App = () => {
  const[good, setGood] = useState(0)
  const[neutral, setNeutral] = useState(0)
  const[bad, setBad] = useState(0)
  const[total, setTotal] = useState(0)
  const[percentage, setPercentage] = useState(0)

  const addRatingGood = () => {
    setGood(good + 1)
    setTotal(total + 1)
    calculatePercentage()
  }
  const addRatingNeutral = () => {
    setNeutral(neutral + 1)
    setTotal(total + 1)
    calculatePercentage()

  }
  const addRatingBad = () => {
    setBad(bad + 1)
    setTotal(total + 1)
    calculatePercentage()

  } 
  const calculatePercentage = () => {
    setPercentage(good/total)
    const percPerc = (
      percentage* 100
    )

  }


  return(
    <div>
      <h1>Give Feedback </h1>
      <Button handleClick={addRatingGood} text={'Good'} />
      <Button handleClick={addRatingNeutral} text={'Neutral'} />
      <Button handleClick={addRatingBad} text={'Bad'} />

      <h1>Statistics</h1>
      <Display value={good} label={'Good: '} />
      <Display value={neutral} label={'Neutral: '} />
      <Display value={bad} label={'Bad: '} />
      <Display value={total} label={'Total feedbacks: '} />
      <Display value={percentage} label={'Percentage of good ratings: '} />
    </div>
  )

}

export default App;
