import { useState } from 'react'

const Display = ({value, label}) => <div>{label} {value}</div>
const Button = ({handleClick, text}) => <button onClick={handleClick}>{text}</button>
const Average = ({score, totalFb}) => `Average of the evaluations scores: ${score/totalFb}`
const PercentageCalc = ({good, totalFb}) => `Percentage of good evaluations: ${good/totalFb * 100} %`

const Statistics = (props) => {

}

const App = (evaluations) => {
  const[good, setGood] = useState(0)
  const[neutral, setNeutral] = useState(0)
  const[bad, setBad] = useState(0)
  const[total, setTotal] = useState(0)
  const[score, setScore] = useState(0)

  const addRatingGood = () => {
    setGood(good + 1)
    setTotal(total + 1)
    setScore(score + 1)
  }
  const addRatingNeutral = () => {
    setNeutral(neutral + 1)
    setTotal(total + 1)
  }
  const addRatingBad = () => {
    setBad(bad + 1)
    setTotal(total + 1)
    setScore(score - 1)
  } 
  return(
    <div>
      <h1>Give Feedback </h1>
      <Button handleClick={addRatingGood} text={'Good'} />
      <Button handleClick={addRatingNeutral} text={'Neutral'} />
      <Button handleClick={addRatingBad} text={'Bad'} />
      <Display value={good} label={'Good: '} />
      <Display value={neutral} label={'Neutral: '} />
      <Display value={bad} label={'Bad: '} />
      <h1>Statistics </h1>
      <Display value={total} label={'Total feedbacks: '} />
      <Average score={score} totalFb={total} />
      <div><PercentageCalc good={good} totalFb={total} /> </div>
    </div>
  )
}

export default App;
