import { useState } from 'react'

const Button = ({handleClick, text}) => (
  <button onClick={handleClick}>{text}</button>
)

const App = () => {
  const [value, setValue] = useState(10)

  const reset = () => 
  setValue(0)

  return(
    <div>
      <p>{value}</p>
      <Button handleClick={reset} text={'Reset'} />
    </div>
  )
}

export default App;
